  public static void writJsonTemplate(String json, StringBuffer tembuffer, StringBuffer modelBuffer)
  {
    try
    {
      modelBuffer.append("package com.demo.travelsociety.activites.model;");
      modelBuffer.append("\n\t");
      modelBuffer.append("import android.os.Parcel;");
      modelBuffer.append("\n\t");
      modelBuffer.append("import android.os.Parcelable;");
      modelBuffer.append("\n\t");

      tembuffer.append("private List getUrlData(JSONObject msgbody) {");
      tembuffer.append("\n\t");
      tembuffer.append("List datas=new ArrayList();");
      tembuffer.append("\n\t");

      tembuffer.append("try {");

      JSONObject jsonObject = new JSONObject(json);
      JSONObject msgbody = jsonObject.getJSONObject("msgbody");
      parseObject(msgbody, tembuffer, "msgbody", modelBuffer);

      tembuffer.append("} catch (Exception e) {");
      tembuffer.append("\n\t");
      tembuffer.append("e.printStackTrace();");
      tembuffer.append("\n");
      tembuffer.append("}");
      tembuffer.append("\n\t");
      tembuffer.append(" return datas; ");
      tembuffer.append("}");

      System.out.println(tembuffer.toString());
      System.out.println("=======================================================\n");
      System.out.println(modelBuffer.toString());
    }
    catch (JSONException e)
    {
      e.printStackTrace();
    }
  }

  public static void parseArray(JSONArray jsonArray, StringBuffer tembuffer, String parent, StringBuffer modelbuffer) {
    try {
      if ((jsonArray != null) && (jsonArray.length() > 0)) {
        JSONObject object = (JSONObject)jsonArray.get(0);
        parseObject(object, tembuffer, parent, modelbuffer);
      }
    } catch (JSONException e) {
      e.printStackTrace();
    }
  }

  public static void parseObject(JSONObject object, StringBuffer tembuffer, String parent, StringBuffer modelbuffer)
  {
    try
    {
      Iterator keys = object.keys();
      while (keys.hasNext())
      {
        String key = keys.next().toString();
        Object obj = object.get(key);
        System.out.print(key + "\n");
        if ((obj instanceof Integer)) {
          System.out.println("整数");
          tembuffer.append("\t");
          modelbuffer.append("private Integer " + key + ";\n\t");
          tembuffer.append("int " + key + " =" + parent + ".getInt(\"" + key + "\");");
          tembuffer.append("\n\t");
          tembuffer.append("\n\t");
          tembuffer.append(parent.substring(0, parent.length() - 3) + "Model.set" + key.substring(0, 1).toUpperCase() + key.substring(1) + "(" + key + ");");
        }
        else if ((obj instanceof Float)) {
          System.out.println("小数");
          tembuffer.append("\t");
          modelbuffer.append("private Float " + key + ";\n\t");
          tembuffer.append("int " + key + " =" + parent + ".getInt(\"" + key + "\");");
          tembuffer.append("\n\t");
          tembuffer.append("\n\t");
          tembuffer.append(parent.substring(0, parent.length() - 3) + "Model.set" + key.substring(0, 1).toUpperCase() + key.substring(1) + "(" + key + ");");
        }
        else if ((obj instanceof String)) {
          tembuffer.append("\t");
          modelbuffer.append("private String " + key + ";\n\t");
          tembuffer.append("String " + key + " =" + parent + ".getString(\"" + key + "\");");
          tembuffer.append("\n\t");
          tembuffer.append(parent.substring(0, parent.length() - 3) + "Model.set" + key.substring(0, 1).toUpperCase() + key.substring(1) + "(" + key + ");");

          tembuffer.append("\n\t");
          System.out.println("字符串");
        } else if ((obj instanceof JSONArray)) {
          System.out.println("数组");

          modelbuffer.append("public class " + key.substring(0, 1).toUpperCase() + key.substring(1) + "Model {");
          modelbuffer.append("\n\t");
          System.out.println(" " + key + " " + key + " = new " + key + "();");
          String temp = "JSONArray " + key + "=" + parent + ".getJSONArray(\"" + key + "\");";
          temp = temp + "\n\t";
          tembuffer.append("\n\t");
          temp = temp + "if(" + key + "!=null&&" + key + ".length()>0){";

          temp = temp + "\n\t";
          temp = temp + "\tfor(int i=0;i<" + key + ".length();i++){";
          temp = temp + "\n\t";
          temp = temp + "\t\tJSONObject " + key + "Obj=(JSONObject)" + key + ".get(i);";
          temp = temp + "\n\t";

          temp = temp + "\t\t" + key.substring(0, 1).toUpperCase() + key.substring(1) + "Model " + key + "Model = new " + key.substring(0, 1).toUpperCase() + key.substring(1) + "Model();";
          temp = temp + "\n\t";

          tembuffer.append(temp);
          tembuffer.append("\n\t");

          parseArray((JSONArray)obj, tembuffer, key + "Obj", modelbuffer);
          tembuffer.append("datas.add(" + key + "Model);");
          tembuffer.append("\n\t");

          tembuffer.append("}");
          tembuffer.append("\n");
          tembuffer.append("}");
          modelbuffer.append("}\n\t");
        } else if ((obj instanceof JSONObject)) {
          modelbuffer.append("public class " + key.substring(0, 1).toUpperCase() + key.substring(1) + " implements Parcelable{");
          modelbuffer.append("\n\t");
          tembuffer.append("JSONObject " + key + " =" + parent + ".getJSONObject(\"" + key + "\");");
          tembuffer.append("\n\t");

          System.out.println("对象");
          parseObject((JSONObject)obj, tembuffer, key, modelbuffer);
          modelbuffer.append("}\n\t");
        }
      }
    }
    catch (JSONException e)
    {
      e.printStackTrace();
    }
  }
